import { useEffect } from "react";
import { Program } from "@project-serum/anchor";
import {
  useAnchorWallet,
  useConnection,
  useWallet
} from "@solana/wallet-adapter-react";

import { createContext, useContext, useMemo, useState } from "react";
import idl from "../idl.json";
import { utf8 } from '@project-serum/anchor/dist/cjs/utils/bytes'
import { findProgramAddressSync } from '@project-serum/anchor/dist/cjs/utils/pubkey'
import { PublicKey } from "@solana/web3.js";

const PROGRAM_KEY = new PublicKey(idl.metadata.address);

const RankContext = createContext();

function getProgram(provider) {
    console.log("provider:", provider)
    return new Program(idl, PROGRAM_KEY, provider);
  }

export const useRank = () => {
    const context = useContext(RankContext);
    if (!context) {
        throw new Error("Missing rank context");
    }
    return context;
}

export const RankProvider = ({ children }) => {
    //const [topUsers, setTopUsers] = useState([]);

    const anchorWallet = useAnchorWallet();
    const { connection } = useConnection();
    const { publicKey } = useWallet();
    const { score, setScore } = useState(0);

    const program = useMemo(() => {
        if (!anchorWallet || !connection) return null;
        return getProgram(anchorWallet);
    }, [
        anchorWallet,
    ]);

    useEffect(() => {

        const start = async () => {
          if (program && publicKey) {
            try {
              const [userPda] = await findProgramAddressSync([utf8.encode('user')], program.programId)
              console.log("data:", userPda.toString())
              const userData = await program.account.boardAccount.all(userPda)
              if (userData) {
                setScore(userData.scores)
                console.log('User data:', userData)
              }
            } catch (error) {
              console.log(error)
            }
          }
        }

        start()

      }, [program, publicKey]);
    return (
      <RankContext.Provider value={score}>{children}</RankContext.Provider>
    )
}
