import React, { useEffect, useState } from 'react';
import twitterLogo from './assets/twitter-logo.svg';
import './App.css';
import RankContainer from './components/RankContainer';
import idl from './idl.json';
import { RankProvider } from './context/Rank';

// Constants
const TWITTER_HANDLE = '_buildspace';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const App = () => {
  const [walletAddress, setWalletAddress] = useState(null);

  const checkIfWalletIsConnected = async () => {
    // We're using optional chaining (question mark) to check if the object is null
      if (window?.solana?.isPhantom) {
        console.log('Phantom wallet found!');
        const response = await window.solana.connect({ onlyIfTrusted: true })
        console.log(
          'Connected with Public Key:',
          response.publicKey.toString()
        );
        setWalletAddress(response.publicKey.toString());
      } else {
        alert('Solana object not found! Get a Phantom Wallet ðŸ‘»');
      }
    };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log('Connected with Public Key:', response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
    }
  };

  const renderNotConnectedContainer = () => (
    <button
      className="cta-button connect-wallet-button"
      onClick={connectWallet}
    >
      Connect to Wallet
    </button>
  );

  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener('load', onLoad);
    return () => window.removeEventListener('load', onLoad);
  }, []);
  /*
   * We want to render this UI when the user hasn't connected
   * their wallet to our app yet.
   */

    const topUsers = [
    {rank: 1, name: "ds", score: 420},
    {rank: 2, name: "22", score: 234},
    {rank: 3, name: "33", score: 123},
    {rank: 4, name: "44", score: 234},
    {rank: 5, name: "55", score: 123},
    {rank: 6, name: "66", score: 234},
    {rank: 7, name: "77", score: 123},
    {rank: 8, name: "88", score: 234},
    {rank: 9, name: "99", score: 123},
    {rank: 10, name: "100", score: 234},
  ]
  return (
    <div className="App">
      <div className="container">
        <div className="header-container">
          <p className="header">
            Top Users âœ¨
          </p>


          {!walletAddress && renderNotConnectedContainer()}
          <RankProvider>
            <RankContainer topUsers={topUsers}></RankContainer>
          </RankProvider>
        </div>

        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`built on @${TWITTER_HANDLE}`}</a>
        </div>
      </div>
    </div>
  );
};

export default App;
