import { useEffect } from "react";
import { Program } from "@project-serum/anchor";
import {
  useAnchorWallet,
  useConnection,
  useWallet
} from "@solana/wallet-adapter-react";

import { createContext, useContext, useMemo, useState } from "react";
import idl from "../idl.json";
import { utf8 } from '@project-serum/anchor/dist/cjs/utils/bytes'
import { findProgramAddressSync } from '@project-serum/anchor/dist/cjs/utils/pubkey'
import { PublicKey } from "@solana/web3.js";
import { AnchorProvider } from "@project-serum/anchor";
import { getLotteryAddress, getMasterAddress, getTicketAddress, getTotalPrize} from "../functions/utils"
import { SystemProgram } from "@solana/web3.js";


const PROGRAM_KEY = new PublicKey(idl.metadata.address);
const LotteryContext = createContext();



export const useLottery = () => {
    const context = useContext(LotteryContext);
    if (!context) {
        throw new Error("Missing Lottery context");
    }
    return context;
}

export const LotteryProvider = ({ children }) => {
    //const [topUsers, setTopUsers] = useState([]);
    const [provider, setProvider] = useState();
    const wallet = useWallet();
    const anchorWallet = useAnchorWallet();
    const { connection } = useConnection();
    const { publicKey } = useWallet();

    const [ score, setScore ] = useState(1);
    const [ masterAddress, setMasterAddress ] = useState();
    const [ ticketAddress, setTicketId ] = useState();
    const [ lotteryAddress, setLotteryAddress] = useState();
    const [ lotteryId, setLotteryId ] = useState();
    const [ lotteryData, setLotteryData ] = useState();
    const [ masterData, setMasterData ] = useState(0);

    const program = useMemo(() => {
        if (anchorWallet) {
            const provider = new AnchorProvider(connection, anchorWallet, AnchorProvider.defaultOptions());
            return new Program(idl, PROGRAM_KEY, provider)
        }
    }, [anchorWallet, connection]);

    useEffect(() => {

        const start = async () => {
            console.log("Starting")
            console.log("Program:", program)
            console.log("Public key:", publicKey)
          if (program && publicKey) {
            try {
                console.log("Program and public key exist")
                const mAddress = await getMasterAddress()
                console.log("masterAddress:", mAddress.toString())
                setMasterAddress(mAddress)
                const mData = await program.account.master.fetch(masterAddress)
                if (mData) {
                    console.log("okk")
                    setMasterData(mData.lastId+1)
                    console.log("mdata: ", masterData)
                    const lAddress = await getLotteryAddress(masterData)
                    console.log('LotteryAddress:', lAddress.toString())
                    setLotteryAddress(lAddress)
                    const lotteryData = await program.account.lottery.fetch(lotteryAddress)
                    if (lotteryData) {
                        console.log(lotteryData)
                        setLotteryData(lotteryData)
                    } else {
                        console("lotteryData not found")
                    }
                }

            } catch (error) {
              console.log(error)
            }
          }
        }

        start()

      }, [program, publicKey]);



    function createLottery(program, wallet) {
        console.log("Creating lottery")
        program.rpc.createLottery({
            accounts: {
                authority: wallet.publicKey,
            },
            signers: [wallet.publicKey],
        });
    }

    const initMaster = async () => {

      if (program && publicKey) {
        console.log("Program and public key exist")
        try {
          const [userPda] = findProgramAddressSync([utf8.encode('master')], program.programId)
            console.log("data:", userPda.toString())
          await program.methods
            .initMaster()
            .accounts({
              master: userPda,
              payer: publicKey,
            })
            .rpc()
            console.log("Master account initialized")
        } catch (error) {
          console.log(error)
        } 
      }
    }

    const buyTicket = async () => {
        if (program && publicKey) {
            try {

            await program.methods
                .buyTicket(lotteryId, score)
                .accounts({
                lottery: lotteryAddress,
                ticket: await getTicketAddress(lotteryAddress, lotteryData.lastTicketId + 1),
                buyer: publicKey,
                systemProgram: SystemProgram.programId,
                })
                .rpc()
                console.log("Ticket bought")
            } catch (error) {
            console.log(error)
            }
        }
    }

    return (
      <LotteryContext.Provider value={{score, buyTicket}}>{children}</LotteryContext.Provider>
    )
}
