import React, { useEffect, useState, useMemo } from 'react';
import logo from '../assets/logo.png';
import '../App.css';
import RankContainer from '../components/RankContainer';
import idl from "../idl.json"
import { Program } from "@project-serum/anchor"
import { useConnection, useAnchorWallet } from "@solana/wallet-adapter-react";
import { PublicKey } from '@solana/web3.js';

const PROGRAM_KEY = new PublicKey(idl.metadata.address);

// Constants
const TWITTER_LINK = `https://twitter.com/Game2049`;
const LEADER_BOARD_SIZE = 10;

function getProgram(provider) {
    console.log("provider:", provider)
    return new Program(idl, PROGRAM_KEY, provider);
  }

export const Rank = () => {
    const {score, lotteryData, ticketData, buyTicket, claimPrize} = useLottery();
    const provider = useAnchorWallet();
    const connection = useConnection();

    const program = useMemo => {
        if (!provider || !connection) return null;
        return getProgram(provider);
    }

    useEffect(() => {
        let temp = []
        for (let i = 0; i < LEADER_BOARD_SIZE; i++) {
            temp.push({rank: i+1, name: lotteryData.account.ids[i], score:lotteryData.account.score[i]})
        }
    }, [lotteryData])



    const topUsers = [
    {rank: 1, name: "ds", score: 420},
    {rank: 2, name: "22", score: 234},
    {rank: 3, name: "33", score: 123},
    {rank: 4, name: "44", score: 234},
    {rank: 5, name: "55", score: 123},
    {rank: 6, name: "66", score: 234},
    {rank: 7, name: "77", score: 123},
    {rank: 8, name: "88", score: 234},
    {rank: 9, name: "99", score: 123},
    {rank: 10, name: "100", score: 234},
  ]
  return (
    <div className="App">
      <div className="container">
        <div className="header-container">
          <p className="header">
            Leader Board âœ¨
          </p>
          <RankContainer topUsers={topUsers}></RankContainer>
        </div>

        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={logo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`please follow us for more information`}</a>
        </div>
      </div>
    </div>
  );
};
