import { useEffect } from "react";
import { Program } from "@project-serum/anchor";
import {
  useAnchorWallet,
  useConnection,
  useWallet
} from "@solana/wallet-adapter-react";

import { createContext, useContext, useMemo, useState } from "react";
import idl from "../idl.json";
import { utf8 } from '@project-serum/anchor/dist/cjs/utils/bytes'
import { findProgramAddressSync } from '@project-serum/anchor/dist/cjs/utils/pubkey'
import { PublicKey } from "@solana/web3.js";
import { AnchorProvider } from "@project-serum/anchor";

const PROGRAM_KEY = new PublicKey(idl.metadata.address);



export const useLottery = () => {
    const context = useContext(LotteryContext);
    if (!context) {
        throw new Error("Missing Lottery context");
    }
    return context;
}

export const LotteryProvider = ({ children }) => {
    //const [topUsers, setTopUsers] = useState([]);
    const [provider, setProvider] = useState();
    const wallet = useWallet();
    const LotteryContext = createContext();
    const { connection } = useConnection();
    const { publicKey } = useWallet();
    const [ score, setScore ] = useState();
    const [ user, setUser ] = useState();
    const [ lottery, setLottery ] = useState();
    const anchorWallet = useAnchorWallet();

    const program = useMemo(() => {
        if (anchorWallet) {
            const provider = new AnchorProvider(connection, anchorWallet, {});
            return new Program(idl, PROGRAM_KEY, provider)
        }
    }, [anchorWallet, connection]);

    useEffect(() => {
      if (wallet) {
        const provider = new AnchorProvider(connection, wallet, {});
        setProvider(provider);
      }
    }, [connection, wallet]);

    useEffect(() => {

        const start = async () => {
          if (program && publicKey) {
            try {
              const [userPda] = await findProgramAddressSync([utf8.encode('master')], program.programId)
              console.log("data:", userPda.toString())
              const userData = await program.account.boardAccount.fetch(userPda)
              if (userData) {
                console.log('User data:', userData)
              }
            } catch (error) {
              console.log(error)
            }
          }
        }

        start()

      }, [program, publicKey]);


    useEffect(() => {
      initMaster()
      // console.log("Master account initialized")
    }, [program, publicKey]);

    function createLottery(program, wallet) {
        console.log("Creating lottery")
        program.rpc.createLottery({
            accounts: {
                authority: wallet.publicKey,
            },
            signers: [wallet.publicKey],
        });
    }

    const initMaster = async () => {
      if (program && publicKey) {
        try {
          const [userPda] = findProgramAddressSync([utf8.encode('master')], program.programId)
            console.log("data:", userPda.toString())
          await program.methods
            .initMaster()
            .accounts({
              master: userPda,
              payer: publicKey,
            })
            .rpc()
            console.log("Master account initialized")
        } catch (error) {
          console.log(error)
        }
      }
    }

    const buyTicket = async () => {
        if (program && publicKey) {
            try {
            const [userPda] = findProgramAddressSync([utf8.encode('master')], program.programId)

            await program.methods
                .buyTicket()
                .accounts({
                master: userPda,
                payer: publicKey,
                })
                .rpc()
                console.log("Ticket bought")
            } catch (error) {
            console.log(error)
            }
        }
        }
    return (
      <LotteryContext.Provider value={{score, buyTicket}}>{children}</LotteryContext.Provider>
    )
}
